mapper.update:
update atsast.contest
set pushlink = #{pushLink}
where contest_id = #{contestId}
  and enable = 1

mapper.select:
select file_description, file_limit
from atsast.file_std
where stage_id = #{stageId}
  and contest_id = #{contestId}

mapper.update:
update atsast.contest
set curr_admin=#{curr}
where contest_id = #{contestId}
  and enable = 1

update atsast.contest
set comment=#{comment}
where contest_id = #{contestId}
  and enable = 1

update atsast.contest
set judging=#{judging}
where contest_id = #{contestId}
  and enable = 1
